import CardView from "./card.view";
import { useState, useEffect } from "react";

const Card = ({ users, animal }) => {

  const [showMore, setShowMore] = useState(false);
  const [usersUpdated, setUsersUpdated] = useState(false);

  let usersFiltered = [];

  const filterByAnimal = (users, animal) => {
    users.map(user => {
      if (user.animals.includes(animal) && user.isActive) {
        usersFiltered.push(user);
      }
    });
  }

  const showMoreClick = () => setShowMore(!showMore);

  const removeUser = (index) => {
    buildUsers(usersFiltered.splice(index, 1));
    usersFiltered = usersFiltered.splice(index, 1);
    setUsersUpdated(true);
  };

  const sortByPoints = (users) => users.sort((a, b) => (a.points > b.points) ? -1 : 1);

  const buildUsers = (users) => {
    filterByAnimal(users, animal);
    sortByPoints(users);
  }

  if (users.length && animal) {
    buildUsers(users);
  }

  useEffect(() => {
    if (users.length && animal) {
      debugger;
      buildUsers(usersFiltered);
      setUsersUpdated(false);
    }
  }, [usersUpdated]);

  return usersFiltered.length ? 
    <CardView 
      users={usersFiltered} 
      animal={animal}
      showMore={showMore}
      removeUser={removeUser}
      showMoreClick={showMoreClick}
    /> 
      : 
    <div>loading...</div>;
}

export default Card;
